import React, { useState } from 'react';
import './App.css';
//using useState to initialize state for 
function App() {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: ''
  });

  const [errors, setErrors] = useState({});

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const errors = {};

    // Validation
    if (!formData.firstName.match(/^[a-zA-Z]+$/)) {
      errors.firstName = 'First name must contain only letters';
    }
    if (!formData.lastName.match(/^[a-zA-Z]+$/)) {
      errors.lastName = 'Last name must contain only letters';
    }
    if (!formData.email.includes('@')) {
      errors.email = 'Email must contain an "@" symbol';
    }

    // If there are errors, set them and return
    if (Object.keys(errors).length > 0) {
      setErrors(errors);
      return;
    }

    // If validation passes, clear errors and submit form data
    setErrors({});
    // Here you can handle form submission, e.g., sending data to a server
    console.log('Form submitted:', formData);
  };

  return (
    <div>
      <h1>Basic Form with Validation</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>First Name:</label>
          <input
            type="text"
            name="firstName"
            value={formData.firstName}
            onChange={handleChange}
          />
          {errors.firstName && <span style={{ color: 'red' }}>{errors.firstName}</span>}
        </div>
        <div>
          <label>Last Name:</label>
          <input
            type="text"
            name="lastName"
            value={formData.lastName}
            onChange={handleChange}
          />
          {errors.lastName && <span style={{ color: 'red' }}>{errors.lastName}</span>}
        </div>
        <div>
          <label>Email:</label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
          />
          {errors.email && <span style={{ color: 'red' }}>{errors.email}</span>}
        </div>
        <div>
          <button type="submit">Submit</button>
          <button type="button" onClick={() => setFormData({ firstName: '', lastName: '', email: '' })}>
            Clear
          </button>
        </div>
      </form>
    </div>
  );
}

export default App;
